package testSel;

public class Ejem_LoopWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		
		//Salir del loop cuando "a" sea mayor que 4
		while(a <= 4) {
			System.out.println("el valos de A: "+ a);
			a++;//Incrementamos el valor de "a" para la siguiente iteracion
		}

	}

}
